import styled, { css } from 'styled-components'

const checkoutItemDetails = css`
  width: 23%;
`

const arrow = css`
  cursor: pointer;
`

export const CheckoutItemContainer = styled.div`
  width: 100%;
  display: flex;
  min-height: 100px;
  border-bottom: 1px solid darkgrey;
  padding: 15px 0;
  font-size: 20px;
  align-items: center;
`

export const ImageContainer = styled.div`
  width: 23%;
  padding-right: 15px;

  img {
    width: 100%;
    height: 100%;
  }
`

export const NameContainer = styled.span`
  ${checkoutItemDetails}
`

export const QuantityContainer = styled.span`
  display: flex;
  ${checkoutItemDetails}
  ${arrow}
`

export const ValueContainer = styled.span`
  margin: 0 10px;
  ${checkoutItemDetails}
`


